  };

  return (
    <>
      {/* <BackDrop classes={classes} openBackDrop={openBackDrop} /> */}
      <Grid container component="main" className={classes.root}>
        <CssBaseline />
        <Grid item xs={false} sm={4} md={7} className={classes.image} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <div className={classes.paper}>
            <Avatar className={classes.avatar}>
              <LockOutlined />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign in
            </Typography>
            <form className={classes.form} onSubmit={submitHandler}>
              <TextField
                // error={errorEmail || errorEmptyEmail}
                variant="outlined"
                margin="normal"
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                autoFocus
                // value={email}
                // onChange={(e) => emailChangeHandler(e.target.value)}
                // helperText={
                //   (errorEmptyEmail && 'Bạn chưa nhập Email') ||
                //   (errorEmail && 'Email không hợp lệ !!!')
                // }
              />
              <TextField
                // error={errorEmptyPassword || errorPassword}
                variant="outlined"
                margin="normal"
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                // value={password}
                // onChange={(e) => passwordChangeHandler(e.target.value)}
                // helperText={
                //   (errorEmptyPassword && 'Bạn chưa nhập Mật khẩu') ||
                //   (errorPassword && 'Mật khẩu phải lớn hơn 6 kí tự !!!')
                // }
              />
              {/* {errorLogin && (
                <p style={{ marginTop: '8px', color: 'red' }}>
                  Tài khoản không hợp lệ !!!
                </p>
              )} */}
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                className={classes.submit}
              >
                Đăng nhập
              </Button>
              <Button
                fullWidth
                variant="contained"
                color="primary"
                className={classes.guest}
                type="button"
                onClick={() => {
                  handleOpen();
                  setTimeout(() => {
                    history.push('/');
                    // dispatch(actionsLogin.clearLogin());
                    // dispatch(actionsHome.setIsLogin());
                  }, 500);
                }}
              >
                Mua hàng không đăng nhập
              </Button>
              <Grid container>
                <Grid item xs>
                  <Link href="/" variant="body2">
                    Forgot password?
                  </Link>
                </Grid>
                <Grid item>
                  <Link href="/register" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>
              <Box mt={5}>
                <Copyright />
              </Box>
            </form>
          </div>
        </Grid>
      </Grid>
    </>
  );
};

export default Login;
